import { useEffect } from "react";
import axios from 'axios'
import { useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import Keycloak from 'keycloak-js';
import { useState } from "react";
import { createContext } from "react";


export const AuthContext = createContext();



const keycloak = new Keycloak({
  url: 'http://localhost:8090/auth',
  realm: 'LibraryAppRealm',
  clientId: 'LibraryAppClient',
});


export const AuthProvider = ({ children }) => {
    const [keycloakInstance, setKeycloakInstance] = useState(null);
    const [authenticated, setAuthenticated] = useState(false);
  
    useEffect(() => {
      keycloak.init({ onLoad: 'login-required' }).then(authenticated => {
        setKeycloakInstance(keycloak);
        setAuthenticated(authenticated);
      });
    }, []);
  
    return (
      <AuthContext.Provider value={{ keycloak: keycloakInstance, authenticated }}>
        {children}
      </AuthContext.Provider>
    );
  };



export default function Logout(){

    const navigate = useNavigate()

    function clearAllCookies() {
        const allCookies = Cookies.get();
        for (let cookie in allCookies) {
          Cookies.remove(cookie);
        }
      }

    useEffect(() => {
        // window.location.assign(`http://localhost:8090/realms/LibraryAppRealm/protocol/openid-connect/logout/logout-confirm?client_id=account`)
        // window.location.assign(`http://localhost:3000/`)

        // clearAllCookies()
        if(keycloak)
            keycloak.logout()


        
        // window.location.assign(`http://localhost:8080`)
        
        
    }, []);



}
